<template>    <v-container>        <v-layout row>            <v-flex xs12 sm6 offset-sm3>                <v-card>                    <v-card-text>                        <v-container>                            <form @submit.prevent="onSignup">                                <v-layout row>                                    <v-flex xs12>                                        <v-text-field                                                v-validate="'required|max:10'"                                                v-model="name"                                                :counter="10"                                                :error-messages="errors.collect('name')"                                                label="Name"                                                data-vv-name="name"                                                required                                        ></v-text-field>                                    </v-flex>                                </v-layout>                                <v-layout row>                                    <v-flex xs12>                                        <v-text-field                                                v-validate="'required|email'"                                                v-model="email"                                                :error-messages="errors.collect('email')"                                                label="E-mail"                                                data-vv-name="email"                                                required                                        ></v-text-field>                                    </v-flex>                                </v-layout>                                <v-layout row>                                    <v-flex xs12>                                        <v-text-field                                                v-validate="'required|min:6'"                                                v-model="password"                                                :error-messages="errors.collect('password')"                                                label="Password"                                                data-vv-name="password"                                                type="password"                                                required                                        ></v-text-field>                                    </v-flex>                                </v-layout>                                <v-layout row>                                    <v-flex xs12>                                        <v-text-field                                                v-validate="'required|min:6'"                                                v-model="password_confirmation"                                                :error-messages="errors.collect('password_confirmation')"                                                label="Confirm Password"                                                data-vv-name="password_confirmation"                                                type="password"                                                required                                        ></v-text-field>                                    </v-flex>                                </v-layout>                                <v-layout row>                                    <v-flex xs12>                                        <v-btn type="submit">Sign up</v-btn>                                    </v-flex>                                </v-layout>                            </form>                        </v-container>                    </v-card-text>                </v-card>            </v-flex>        </v-layout>    </v-container></template><script>    export default {        name: "Signup",        data () {            return {                email: '',                password: '',                password_confirmation: '',                name: '',            }        },        methods: {            onSignup () {                this.$validator.validateAll().then((result) => {                    if (result) {                        this.$store.dispatch('onSignup', {                            name: this.name,                            email: this.email,                            password: this.password,                            password_confirmation: this.password_confirmation                        }) .then(() => {                            this.$snotify.info("Jeni loguar me sukses", {                                timeout: 2000,                                showProgressBar: false,                                closeOnClick: false,                                pauseOnHover: true                            });                            this.$router.push({ name: "home" });                        })                            .catch(error => {                                this.$snotify.error(error.response.data.error, {                                    timeout: 2000,                                    showProgressBar: false,                                    closeOnClick: false,                                    pauseOnHover: true                                });                                setTimeout(function() {                                    location.reload();                                }, 5000);                            });                    }                    console.log('correct errors')                })            }        }    }</script><style scoped></style>