<template>    <v-container>        <v-layout justify-center>            <v-flex xs12 sm10 md8 lg6>                <v-card color="blue-grey darken-2" class="white--text">                    <v-card-title>                        <div class="headline">Activate your account</div>                    </v-card-title>                    <v-card-actions>                        <form @submit.prevent="activateAccount">                            <v-btn flat dark type="submit">Activate now</v-btn>                        </form>                    </v-card-actions>                </v-card>            </v-flex>        </v-layout>    </v-container></template><script>    export default {        name: "VerifyEmail",        data() {            return {                token: null            };        },        methods: {            activateAccount() {                this.$store.dispatch("activateAccount", this.token)                    .then(response => {                        this.$snotify.success(response.data.data, {                            timeout: 2000,                            showProgressBar: false,                            closeOnClick: false,                            pauseOnHover: true                        });                        this.$router.push({ name: "login" });                    })                    .catch(error => {                        this.$snotify.error(error.error, {                            timeout: 2000,                            showProgressBar: false,                            closeOnClick: false,                            pauseOnHover: true                        });                    });            }        },        created() {            this.token = this.$route.query.token;        }    }</script><style scoped></style>